custom_scss:
.btn.btn-primary {
    background-color: #365b6d;
    color: var(--white);
    white-space: nowrap;
    --icon-stroke: currentColor;
    --icon-fill-bg: var(--primary);
}

.navbar-light {
    border-bottom: 4px solid #febd59;
    background: #365b6d;
    height: 7.5rem;
}

.web-footer {
    padding: 2rem 0;
    min-height: 140px;
    background-color: #365b6d;
    border-top: 1px solid #e2e6e9;
}

.footer-logo {
    min-width: 5rem;
    height: 5rem;
    object-fit: contain;
    object-position: left;
}

.navbar-light .navbar-nav .nav-link {
    color: #FFFFFF;
    font-size: .875rem;
    font-weight: 800;
}

.navbar-brand img {
    display: inline-block;
    max-width: 350px;
    max-height: 350px;
}

.footer-info {
    color: #c9c9c9;
    font-size: .875rem;
}

.ql-editor {
    font-family: var(--font-stack);
    color: #c9c9c9;
}

